mapper.select:
SELECT COUNT(*) AS cnt
FROM member
WHERE loginId = #{loginId}

SELECT *
FROM member
WHERE name = #{name}
AND email = #{email}

mapper.update:
UPDATE member
updateDate = NOW(), loginPw = #{loginPw},
WHERE id = #{loginedMemberId}

UPDATE member
delDate = NOW(), delStatus = 1,
WHERE id = #{loginedMemberId}
